/** 
  @notice
  Stores operator permissions for all addresses. Addresses can give permissions to any other address to take specific indexed actions on their behalf.
*/
contract JBOperatorStore is IJBOperatorStore {
  //*********************************************************************//
  // --------------------- public stored properties -------------------- //
  //*********************************************************************//

  /** 
    @notice
    The permissions that an operator has to operate on a specific domain.

    @dev
    An account can give an operator permissions that only pertain to a specific domain.
    There is no domain with a value of 0 â€“ accounts can use the 0 domain to give an operator
    permissions to all domains on their behalf.

    _operator The address of the operator.
    _account The address of the account being operated.
    _domain The domain within which the permissions apply.
  */
  mapping(address => mapping(address => mapping(uint256 => uint256))) public override permissionsOf;

  //*********************************************************************//
  // ------------------------- external views -------------------------- //
  //*********************************************************************//

  /** 
    @notice 
    Whether or not an operator has the permission to take a certain action pertaining to the specified domain.

    @param _operator The operator to check.
    @param _account The account that has given out permissions to the operator.
    @param _domain The domain that the operator has been given permissions to operate.
    @param _permissionIndex The permission indexes to check for.

    @return Whether the operator has the specified permission.
  */
  function hasPermission(
    address _operator,
    address _account,
    uint256 _domain,
    uint256 _permissionIndex
  ) external view override returns (bool) {
    if (_permissionIndex > 255) {
      revert PERMISSION_INDEX_OUT_OF_BOUNDS();
    }
    return (((permissionsOf[_operator][_account][_domain] >> _permissionIndex) & 1) == 1);
  }

  /** 
    @notice 
    Whether or not an operator has the permission to take certain actions pertaining to the specified domain.

    @param _operator The operator to check.
    @param _account The account that has given out permissions to the operator.
    @param _domain The domain that the operator has been given permissions to operate.
    @param _permissionIndexes An array of permission indexes to check for.

    @return Whether the operator has all specified permissions.
  */
  function hasPermissions(
    address _operator,
    address _account,
    uint256 _domain,
    uint256[] calldata _permissionIndexes
  ) external view override returns (bool) {
    for (uint256 _i = 0; _i < _permissionIndexes.length; _i++) {
      uint256 _permissionIndex = _permissionIndexes[_i];
      if (_permissionIndex > 255) {
        revert PERMISSION_INDEX_OUT_OF_BOUNDS();
      }
      if (((permissionsOf[_operator][_account][_domain] >> _permissionIndex) & 1) == 0)
        return false;
    }
    return true;
  }

  //*********************************************************************//
  // ---------------------- external transactions ---------------------- //
  //*********************************************************************//

  /**
    @notice
    Sets permissions for an operators.

    @dev
    Only an address can set its own operators.

    @param _operatorData The data that specifies the params for the operator being set.
      @dev _operatorData.operators The operators to whom permissions will be given.
      @dev _operatorData.domains Lists the domain that each operator is being given permissions to operate. A value of 0 serves as a wildcard domain. Applications can specify their own domain system.
      @dev _operatorData.permissionIndexes Lists the permission indexes to set for each operator. Indexes must be between 0-255. Applications can specify the significance of each index.
  */
  function setOperator(JBOperatorData calldata _operatorData) external override {
    // Pack the indexes into a uint256.
    uint256 _packed = _packedPermissions(_operatorData.permissionIndexes);

    // Store the new value.
    permissionsOf[_operatorData.operator][msg.sender][_operatorData.domain] = _packed;

    emit SetOperator(
      _operatorData.operator,
      msg.sender,
      _operatorData.domain,
      _operatorData.permissionIndexes,
      _packed
    );
  }

  /**
    @notice
    Sets permissions for many operators.

    @dev
    Only an address can set its own operators.

    @param _operatorData The data that specifies the params for each operator being set.
      @dev _operatorData.operators The operators to whom permissions will be given.
      @dev _operatorData.domains Lists the domain that each operator is being given permissions to operate. A value of 0 serves as a wildcard domain. Applications can specify their own domain system.
      @dev _operatorData.permissionIndexes Lists the permission indexes to set for each operator. Indexes must be between 0-255. Applications can specify the significance of each index.
  */
  function setOperators(JBOperatorData[] calldata _operatorData) external override {
    for (uint256 _i = 0; _i < _operatorData.length; _i++) {
      // Pack the indexes into a uint256.
      uint256 _packed = _packedPermissions(_operatorData[_i].permissionIndexes);

      // Store the new value.
      permissionsOf[_operatorData[_i].operator][msg.sender][_operatorData[_i].domain] = _packed;

      emit SetOperator(
        _operatorData[_i].operator,
        msg.sender,
        _operatorData[_i].domain,
        _operatorData[_i].permissionIndexes,
        _packed
      );
    }
  }

  //*********************************************************************//
  // --------------------- private helper functions -------------------- //
  //*********************************************************************//

  /** 
    @notice 
    Converts an array of permission indexes to a packed uint256.

    @param _indexes The indexes of the permissions to pack.

    @return packed The packed result.
  */
  function _packedPermissions(uint256[] calldata _indexes) private pure returns (uint256 packed) {
    for (uint256 _i = 0; _i < _indexes.length; _i++) {
      uint256 _index = _indexes[_i];
      if (_index > 255) {
        revert PERMISSION_INDEX_OUT_OF_BOUNDS();
      }
      // Turn the bit at the index on.
      packed |= 1 << _index;
    }
  }
}

contracts/abstract/JBProject.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.6;

import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/Address.sol';

import './../interfaces/IJBDirectory.sol';
import './../libraries/JBTokens.sol';

// --------------------------- custom errors -------------------------- //
//*********************************************************************//
error INSUFFICIENT_BALANCE();
error PROJECT_NOT_FOUND();
error TERMINAL_NOT_FOUND();

/** 
  @notice A contract that inherits from JuiceboxProject can use Juicebox as a business-model-as-a-service.
  @dev The owner of the contract makes admin decisions such as:
    - Which address is the funding cycle owner, which can withdraw funds from the funding cycle.
    - Should this project's Tickets be migrated to a new TerminalV1. 
*/
abstract contract JBProject is Ownable {
  /// @notice The direct deposit terminals.
  IJBDirectory public immutable directory;

  /// @notice The ID of the project that should be used to forward this contract's received payments.
  uint256 public projectId;

  /** 
      @param _projectId The ID of the project that should be used to forward this contract's received payments.
      @param _directory A directory of a project's current Juicebox terminal to receive payments in.
    */
  constructor(uint256 _projectId, IJBDirectory _directory) {
    projectId = _projectId;
    directory = _directory;
  }

  /** 
      Received funds go straight to the project.
    */
  receive() external payable {
    _pay(msg.sender, '', false, JBTokens.ETH);
  }

  /** 
      @notice Allows the project that is being managed to be set.
      @param _projectId The ID of the project that is being managed.
    */
  function setProjectId(uint256 _projectId) external onlyOwner {
    projectId = _projectId;
  }

  /** 
      @notice Make a payment to this project.
      @param _beneficiary The address who will receive tickets from this fee.
      @param _memo A memo that will be included in the published event.
      @param _preferClaimedTokens Whether ERC20's should be claimed automatically if they have been issued.
    */
  function pay(
    address _beneficiary,
    string memory _memo,
    bool _preferClaimedTokens,
    address _token
  ) external payable {
    _pay(_beneficiary, _memo, _preferClaimedTokens, _token);
  }

  /** 
      @notice Take a fee for this project from this contract.
      @param _projectId The ID of the project being funded.
      @param _amount The payment amount.
      @param _beneficiary The address who will receive tickets from this fee.
      @param _memo A memo that will be included in the published event.
      @param _preferClaimedTokens Whether ERC20's should be claimed automatically if they have been issued.
    */
  function _fundTreasury(
    uint256 _projectId,
    uint256 _amount,
    address _beneficiary,
    string memory _memo,
    bool _preferClaimedTokens,
    address _token
  ) internal {
    if (_projectId == 0) {
      revert PROJECT_NOT_FOUND();
    }

    // Find the terminal for this contract's project.
    IJBPaymentTerminal _terminal = directory.primaryTerminalOf(_projectId, _token);

    // There must be a terminal.
    if (_terminal == IJBPaymentTerminal(address(0))) {
      revert TERMINAL_NOT_FOUND();
    }

    // There must be enough funds in the contract to take the fee.
    if (address(this).balance < _amount) {
      revert INSUFFICIENT_BALANCE();
    }

    // Send funds to the terminal.
    _terminal.pay{value: _amount}(
      _projectId,
      _beneficiary,
      0,
      _preferClaimedTokens,
      _memo,
      bytes('')
    );
  }

  /** 
      @notice See the documentation from `pay`.
    */
  function _pay(
    address _beneficiary,
    string memory _memo,
    bool _preferClaimedTokens,
    address _token
  ) private {
    if (projectId == 0) {
      revert PROJECT_NOT_FOUND();
    }

    // Get the terminal for this contract's project.
    IJBPaymentTerminal _terminal = directory.primaryTerminalOf(projectId, _token);

    // There must be a terminal.
    if (_terminal == IJBPaymentTerminal(address(0))) {
      revert TERMINAL_NOT_FOUND();
    }

    _terminal.pay{value: msg.value}(
      projectId,
      _beneficiary,
      0,
      _preferClaimedTokens,
      _memo,
      bytes('')
    );
  }
}

contracts/test/JBFakeProject.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.6;

// Inheritance
import '../abstract/JBProject.sol';

/**
  @dev 
  Fake Juicebox project used for testing.
*/
contract JBFakeProject is JBProject {
  constructor(uint256 _projectId, IJBDirectory _directory) JBProject(_projectId, _directory) {}

  /**
    @dev
    Example API that calls internal _fundTreasury function. The example here is an NFT mint
    function that routes funds to a Juicebox project.
   */
  function mint(
    uint256 _projectId,
    uint256 _amount,
    address _beneficiary,
    string memory _memo,
    bool _preferClaimedTokens,
    address _token
  ) external payable {
    // Mint NFT, etc.
    // ...

    // Fund Juicebox treasury.
    _fundTreasury(_projectId, _amount, _beneficiary, _memo, _preferClaimedTokens, _token);
  }
}
